def merge_the_tools(string, k):
    # your code goes here
    for i in range(0,len(string),k):
        sub = ''
        for j in string[i : k+i]:
            if j not in sub:
                sub += j
        print(sub)
            

if __name__ == '__main__':
    string, k = "AABCAAADA", 3
    # string, k = "", 3452
    

    merge_the_tools(string, k)