def merge_the_tools(s, k):
    dic = {}
    for i in range (1,len(s)+1) :
        dic[s[i-1]]="dummy"
        if i % k == 0 :
            print("".join(dic.keys()))
            dic = {}
            

if __name__ == '__main__':
    string, k = "AABCAAADA", 3
    # string, k = "", 3452
    

    merge_the_tools(string, k)